import Foundation

class Observer<T> {
     var value: T {
        didSet {
            DispatchQueue.main.async {
                self.valueChanged?(self.value)
            }
        }
    }
    
    fileprivate var valueChanged: ((T) -> Void)?
    
    init(value: T) {
        self.value = value
    }
    
     func addObserver(enable: Bool = true, _ onChange: ((T) -> Void)?) {
        valueChanged = onChange
        if enable {
            onChange?(value)
        }
    }
    
    func removeObserver() {
        valueChanged = nil
    }
}
